import React from 'react'
import styled from 'styled-components';

const Div = styled.div`
.container{
    width: 400px;
    min-height: 50px;
    position: fixed;
    top: 20px;
    right: 20px;
    display: none;
    grid-template-columns: 0fr 1fr 0fr; 
    color: white;
    border-radius: 5px;
    box-shadow: 1px 1px 10px 1px lightgrey;
    
    :hover {
        filter: contrast(90%);
    }
    .icon{
        background-repeat: no-repeat;
        background-position: 10px 12px;
        width: 40px;
        height: 100%;
        border-radius: 5px 0px 0px 5px;
    }
    .content {
        padding: 10px;
        width: 330px;
        overflow-x: hidden;
        overflow-wrap: break-word;
        .head {
            font-weight: bolder;
        }
        .body {
            padding-top: 5px;
            font-size: 14px;
        }
    }
    .close {
        border-radius: 5px;
        button {
            margin: 5px;
            background: transparent;
            border: none;
            color: white;
            :hover {
                color: black
            }
        }
    }
}
.container-success {
    background: #38a76a;
    .icon {
        background-color: #449c6b;
        background-image: url('data:image/png;base64,')!important;
    }
}

.container-error {
    background: #b85252;
    .icon {
        background-color: indianred;
        background-image: url('data:image/png;base64,')!important;
    }
}

.container-info {
    background: cadetblue;
    .icon {
        background-color: #508688;
        background-image: url('data:image/png;base64,')!important;
    }
}

.container-warning {
    background: #f89406;
    .icon {
        background-color: #db8102;
        background-image: url('data:image/png;base64,')!important;
    }
}

@keyframes slide_in {
    0% {
        right: -500px;
    }
    100% {
        right: 20px;
    }
}

@keyframes slide_out {
    0% {
        right: 20px;
    }
    100% {
        right: -500px;
    }
}
.animate-in {
    display: grid;
    -webkit-animation: slide_in 0.8s ease forwards ;
    animation: slide_in 0.8s ease forwards;
}
.animate-out {
    display: grid;
    -webkit-animation: slide_out 0.8s ease forwards;
    animation: slide_out 0.8s ease forwards;
    /* animation-delay: 10s; */
}


`
export default function Toast(props: any) {
    const {config} = props;
    return (
        <Div>
            <div className={`container container-${config?.type} ${config?.show ? 'animate-in': '' } ${((config?.show && config?.readyToClose) ? 'animate-out': '')}`}>
                <div className="icon">
                </div>
                <div className="content">
                    <div className="head">{config?.title || ""}</div>
                    <div className="body">{config?.message || "Success"}</div>
                </div>
                <div className="close">
                    <button>x</button>
                </div>
            </div>
        </Div>
    )
}
